<template>
  <div>
    <h1>Directivas</h1>
    <hr/>
    <span v-text="titulo"></span>
    <div v-html="html">
    
  </div>
  <hr/>
  <div>
    <h1>V_SHOW</h1>
    <h3>Tu edad es: {{ edad }}</h3>
    <h2 v-show="edad >= 18">Mayor de edad</h2>
    <h2 v-show="edad < 18">Menor de edad</h2> 
    <button @click="add()">Add</button>
    <button @click="dis()">Dis</button>
  </div>
  <hr/>
  <div>
    <h1>V-IF, v-else-if, v-else</h1>
    <h3>Tu edad es: {{ edad }}</h3>
    <h2 v-if="edad >= 18 && edad > 0 && edad < 100">Mayor de edad</h2>
    <h2 v-else-if="edad < 18 && edad >0 && edad < 100">Menor de edad</h2> 
    <h2 v-else>Edad invalida</h2>
    <button @click="add()">Add</button>
    <button @click="dis()">Dis</button>
  </div>
  <hr/>
  <div>
    <h1>V-FOR</h1>
    <ul>
      <li v-for="(usuario,index) in usuarios" :key="index">{{ index }}- {{usuario}}</li>
    </ul>
  </div>
  <hr/>
  <div @click="click" class="contador" >
    <h1>V-ON</h1>
  <h2> Clicks: {{ clicks }} </h2> 
  </div>
  <hr/>
  <div>
    <h1>V-BIND</h1>
    <img v-bind:src="url">
    <img :src="url">
  </div>
  </div>
  
</template>

<script>
export default {
  data: ()=>({
    titulo:"Soy un v-text",
    html:"<h3>Soy un h3 desde v-html</h3>",
    edad:17,
    usuarios:[
      "Melisa",
      "Jose",
      "Pedrito"
    ],
    clicks:0,
    url:"data:image/jpeg;base64,"
  }),
  methods:{
    click(){
      
      this.clicks++
    },
    add(){
      this.edad++
    },
    dis(){
      this.edad-- 
    }
  }
}
</script>

<style scoped>
.contador{
  background-color: tomato;
  color:snow;
  font-weight: bold;
  height: 200px;
}
div{
  text-align: center;
  margin:0px 2rem
}
</style>